#!/usr/bin/env python
# -*- coding: utf-8 -*-

"""
ERP bot from Goldencalizas.
"""

from telegram.ext import (
    Updater,
    CommandHandler,
    MessageHandler,
    Filters,
    CallbackQueryHandler,
)

from .goldenbot import GoldenBot


def texts(update, context):
    print(111)
    print(update.message.text)
    print(111)


def main():
    """Start the bot."""
    # Create the Updater and pass it your bot's token.
    # Make sure to set use_context=True to use the new context based callbacks
    # Post version 12 this will no longer be necessary
    updater = Updater(
        "5223922019:AAHOdMdzuPHwN9ZH_Xm5ZdWz69UuccwXfyc", use_context=True
    )

    bot = GoldenBot()
    dp = updater.dispatcher

    dp.add_handler(CommandHandler("estiba", bot.estiba))
    dp.add_handler(CommandHandler("e", texts))

    dp.add_handler(CommandHandler("produccion", bot.produccion))
    dp.add_handler(CommandHandler("p", bot.produccion))

    dp.add_handler(CommandHandler("inventario", bot.inventario))
    dp.add_handler(CommandHandler("i", bot.inventario))

    dp.add_handler(CommandHandler("pagos", bot.pago))
    dp.add_handler(CommandHandler("pago", bot.pago))

    dp.add_handler(CommandHandler("fd", bot.fd))
    dp.add_handler(CommandHandler("nofd", bot.nofd))

    dp.add_handler(CommandHandler("anular", bot.anular))
    dp.add_handler(CommandHandler("rm", bot.anular))

    dp.add_handler(CallbackQueryHandler(bot.button_response))
    dp.add_error_handler(bot.log_errors)

    # Run the bot until you press Ctrl-C or the process receives SIGINT,
    # SIGTERM or SIGABRT. This should be used most of the time, since
    # start_polling() is non-blocking and will stop the bot gracefully.
    updater.start_polling()
    updater.idle()


if __name__ == "__main__":
    main()
