def trabajadores(db):

    trabajadores = db.get_trabajadores()
    rows = []
    row = []

    for i, trabajador in enumerate(trabajadores, 1):
        button = str(trabajador[0]) + " " + trabajador[1]
        row.append(InlineKeyboardButton(button, callback_data=trabajador[1]))
        if i % 3 == 0:
            rows.append(row)
            row = []
    rows.append(row)
    rows.append(
        [
            InlineKeyboardButton("âŒCancelar", callback_data="Cancelar"),
            InlineKeyboardButton("âQuitar Ultimo", callback_data="Quitar_trabajador"),
        ]
    )
    rows.append(
        [
            InlineKeyboardButton("ğŸ”¼Nuevo", callback_data="Nuevo"),
            InlineKeyboardButton("â¡Continuar", callback_data="Continuar"),
        ]
    )

    return InlineKeyboardMarkup(rows)


def pago(db):

    trabajadores = db.get_trabajadores()
    rows = []
    row = []

    for i, trabajador in enumerate(trabajadores, 1):
        row.append(
            InlineKeyboardButton(
                " ${:,.2f}".format(db.get_pagos(trabajador[1])) + " " + trabajador[1],
                callback_data=trabajador[1],
            )
        )
        if i % 2 == 0:
            rows.append(row)
            row = []
    rows.append(row)

    return InlineKeyboardMarkup(rows)


