#!/usr/bin/env python3
# -*- coding: utf-8 -*-

from sqlalchemy import Column, Integer, String, Boolean, Float, Numeric
from sqlalchemy.orm import declarative_base


Base = declarative_base()


class Trabajadores(Base):
    __tablename__ = "trabajadores"

    id = Column(Integer, primary_key=True)
    trabajador = Column(String)


class Bodega(Base):
    __tablename__ = "estibas"

    id = Column(Integer, primary_key=True)
    producto = Column(String)
    trabajadores = Column(String)

    fd = Column(Boolean)

    date = Column(String)
    var = Column(Integer)
    total = Column(Integer)


class Pagos(Base):
    __tablename__ = "estibadores"

    id = Column(Integer, primary_key=True)
    trabajador = Column(String)
    tipo = Column(String)

    fd = Column(Boolean)

    date = Column(String)
    var = Column(Numeric)
    total = Column(Numeric)
