#!/usr/bin/env python3

from datetime import datetime as dt


def is_fd():
    if dt.today().weekday() == 6:
        return True

    if dt.today().weekday() == 5 and dt.now() < dt.time(12, 0):
        return True

    return False


def get_price(cmd):
    if cmd == "Estiba":
        price = 0.05
    else:
        price = 0.1

    if is_fd():
        price += 0.02

    return price
